<?php/** * Cookie class *  * @author Gaëtan SAURA <gaetan.saura@gmail.com> * @copyright Gaëtan SAURA * @since 25-02-2014 */class Cookie extends Model{    public $name = "cookie";    static private $_cookie = array();    static public function get($key = null)    {        if (isset(self::$_cookie[Geek::encrypt($key, 'I')])) {            return self::$_cookie[Geek::encrypt($key, 'I')];        } else {            return false;        }    }    static public function set($key, $value)    {        self::$_cookie[Geek::encrypt($key, 'I')] = $value;    }}