<?php/** * Debug class *  * @author Gaëtan SAURA <gaetan.saura@gmail.com> * @copyright Gaëtan SAURA * @since 25-02-2014 */class Debug{    static private $_debug = array();    static private $_xtime = array();    static public function add($str)    {        self::$_debug[] = Geek::sanityze($str);    }    static public function setTime($x = null, $y = null)    {        if (!is_null($x))            self::$_xtime[0] = $x;        if (!is_null($y))            self::$_xtime[1] = $y;    }    static public function showDetails()    {        $title = Traduction::getT('Debug');        $errors = Error::displayErrors(true);        $memory = Memory::useMemory();        $memory->session['execution_time'] = self::$_xtime[1] - self::$_xtime[0];        echo '<hr /><fieldset><legend>' . $title . '</legend><pre><small>';        print('Execution time : ' . $memory->session['execution_time'] . "\n");        print('---' . "\n");        print('Debug' . "\n");        print(implode("\n", self::$_debug) . (!empty(self::$_debug) ? "\n" : ''));        print('---' . "\n");        print('Errors' . "\n");        print(implode("\n", $errors) . (!empty(self::$errors) ? "\n" : ''));        print('---' . "\n");        print('Classes : ' . count($memory->obj) . "\n");        print(implode("\n", array_keys($memory->obj)) . "\n");        print('---' . "\n");        print('Template : ' . count($memory->template) . "\n");        echo '</small></pre>';    }}