<?php/** * Geek class *  * @author Gaëtan SAURA <gaetan.saura@gmail.com> * @copyright Gaëtan SAURA * @since 25-02-2014 */class Geek{    static function encrypt($str, $type = null)    {        switch (strtolower($type)) {            case 's':                return md5(_GSF_SHORTCODE_ . $str);                break;            case 'w':                return md5(_GSF_WSCODE_ . $str);                break;            case 'i':                return md5(_GSF_INTCODE_);                break;            case 'a':                return md5(_GSF_AUTHCODE_ . $str);                break;            case 'r':                return md5(_GSF_GENECODE_ . mt_rand(0, PHP_INT_MAX) . $str);                break;            default:                return md5(_GSF_GENECODE_ . $str);                break;        }    }    static public function sanityze($str)    {        return htmlentities(trim($str));    }}